local discmat_blue = surface.GetTextureID("ricochet/640_discblue")local discmat_blue2 = surface.GetTextureID("ricochet/640_discblue2")local discmat_red = surface.GetTextureID("ricochet/640_discred")local discmat_red2 = surface.GetTextureID("ricochet/640_discred2")local discmat_gray = surface.GetTextureID("ricochet/640_discgray")local discmat_freeze = surface.GetTextureID("ricochet/640_freeze")local discmat_frozen = surface.GetTextureID("ricochet/640_frozen")local discmat_fire = surface.GetTextureID("ricochet/640_fire")local discmat_fast = surface.GetTextureID("ricochet/640_fast")local discmat_triple = surface.GetTextureID("ricochet/640_fast")local NextRotate = 0local CurImage = nillocal DisableHUD = {	"CHudHealth", "CHudBattery", "CHudCrosshair", "CHudAmmo", "CHudSecondaryAmmo", "CHudZoom", "CHudDamageIndicator", "CHudWeaponSelection"}POWERUP_IMAGE[POWERUP_TITLE[POWERUPS["POWERUP_FAST"]]] = discmat_fastPOWERUP_IMAGE[POWERUP_TITLE[POWERUPS["POWERUP_FIRE"]]] = discmat_firePOWERUP_IMAGE[POWERUP_TITLE[POWERUPS["POWERUP_FREEZE"]]] = discmat_freezePOWERUP_IMAGE[POWERUP_TITLE[POWERUPS["POWERUP_TRIPLESHOT"]]] = discmat_blue2function GM:HUDPaint()	if not IsValid(MySelf) then return end	local w, h = ScrW(), ScrH()	local diskdist, hdist = 175, 72	local disk = MySelf:GetActiveWeapon()	local powerups = {}	draw.SimpleText("Round: ".. GetGlobalInt("rc_round"), "GRC_HUDFont", w / 2, 16, COLOR_GREEN, TEXT_ALIGN_CENTER)	draw.SimpleText(util.ToMinutesSeconds(math.max(0, GetGlobalInt("rc_timelimit") - CurTime())), "GRC_HUDFont", w / 2, 48, COLOR_GREEN, TEXT_ALIGN_CENTER)	if IsValid(disk) then		local clip1 = disk:Clip1()		if IsValid(MySelf["status_freeze"]) then			local dietime = MySelf["status_freeze"].DieTime			local sizing = math.cos(CurTime() * 10) * 12			surface.SetDrawColor(COLOR_WHITE)			surface.SetTexture(discmat_frozen)			surface.DrawTexturedRect((w / 2) - 28 + (diskdist * 1.8) - (sizing / 2), h - (96 + (sizing / 2)), 64 + sizing, 64 + sizing)			draw.SimpleTextOutlined(math.ceil(math.abs(dietime - CurTime())), "GRC_HUDFont", (w / 2) + 3 + (diskdist * 1.8), h - 63, COLOR_WHITE, TEXT_ALIGN_CENTER, TEXT_ALIGN_CENTER, 1, COLOR_BLACK)		end		surface.SetDrawColor(COLOR_WHITE)		surface.SetTexture(discmat_gray)		for i = clip1 + 1, 3 do			surface.DrawTexturedRect((w / 2) - 28 - diskdist + (diskdist * (i - 1)), h - hdist, 56, 32)		end				for status, trans in pairs(POWERUP_TITLE) do			if IsValid(MySelf["status_"..status]) then				table.insert(powerups, trans)			end		end		table.sort(powerups)		draw.SimpleTextOutlined(string.AndSeparate(powerups), "Default", w / 2, h - (hdist / 2.75), COLOR_WHITE, TEXT_ALIGN_CENTER,TEXT_ALIGN_CENTER, 1, COLOR_BLACK)		surface.SetDrawColor(COLOR_WHITE)		if MySelf:Team() == TEAM_BLUE then			surface.SetTexture(clip1 == 3 and discmat_blue2 or discmat_blue)		else 			surface.SetTexture(clip1 == 3 and discmat_red2 or discmat_red)		end		if #powerups > 0 then			if CurTime() >= NextRotate then				NextRotate = CurTime() + 1.5				CurImage = table.FindNext(powerups, CurImage)				if not (CurImage == "Power Shot") then					surface.SetTexture(POWERUP_IMAGE[CurImage])				else					surface.SetTexture(MySelf:Team() == TEAM_BLUE and discmat_blue2 or discmat_red2)				end			else				if CurImage and not (CurImage == "Power Shot") then					surface.SetTexture(POWERUP_IMAGE[CurImage])				else					surface.SetTexture(MySelf:Team() == TEAM_BLUE and discmat_blue2 or discmat_red2)				end			end		end		for i=1, clip1 do			surface.DrawTexturedRect((w / 2) - 28 - diskdist + (diskdist * (i - 1)), h - hdist, 56, 32)		end	endendfunction GM:HUDShouldDraw(hud)	if table.HasValue(DisableHUD, hud) then		return false	end	return trueend