local discmat_blue = surface.GetTextureID("ricochet/640_discblue")local discmat_blue2 = surface.GetTextureID("ricochet/640_discblue2")local discmat_red = surface.GetTextureID("ricochet/640_discred")local discmat_red2 = surface.GetTextureID("ricochet/640_discred2")local discmat_gray = surface.GetTextureID("ricochet/640_discgray")local discmat_freeze = surface.GetTextureID("ricochet/640_freeze")local discmat_frozen = surface.GetTextureID("ricochet/640_frozen")local discmat_fire = surface.GetTextureID("ricochet/640_fire")local discmat_fast = surface.GetTextureID("ricochet/640_fast")local DisableHUD = {	"CHudHealth", "CHudBattery", "CHudCrosshair", "CHudAmmo",	"CHudSecondaryAmmo", "CHudZoom", "CHudDamageIndicator", "CHudWeaponSelection"}function GM:HUDPaint()	if not IsValid(MySelf) then return end	local w, h = ScrW(), ScrH()	local disk = MySelf:GetActiveWeapon()	draw.SimpleText("Round: "..GetGlobalInt("rc_round"), "Default", w / 2, 16, COLOR_GREEN, TEXT_ALIGN_CENTER)	draw.SimpleText("Time: "..math.floor(GetGlobalInt("rc_timelimit") - CurTime()), "Default", w / 2, 32, COLOR_GREEN, TEXT_ALIGN_CENTER)	if IsValid(disk) then		local clip1 = disk:Clip1()		local powerup = MySelf:GetPowerup()		local status = MySelf:GetStatus()		local diskdist = 175		local wdist = 0		local hdist = 80		draw.SimpleTextOutlined(POWERUP_TITLE[powerup], "Default", w / 2, h - 32, COLOR_WHITE, TEXT_ALIGN_CENTER, TEXT_ALIGN_CENTER, 1, COLOR_BLACK)		surface.SetDrawColor(COLOR_WHITE)		surface.SetTexture(discmat_gray)		for i = clip1 + 1, 3 do			surface.DrawTexturedRect((w / 2) - 28 - diskdist + (diskdist * (i - 1)), h - hdist, 56, 32)		end		surface.SetDrawColor(COLOR_WHITE)		if powerup == POWERUP_FAST then			wdist, hdist = -6, 85			surface.SetTexture(discmat_fast)		elseif powerup == POWERUP_FREEZE then			surface.SetTexture(discmat_freeze)		elseif powerup == POWERUP_POWERSHOT then			surface.SetTexture(discmat_red2)		elseif powerup == POWERUP_FIRE then			wdist, hdist = 0, 79			surface.SetTexture(discmat_fire)		elseif MySelf:Team() == TEAM_BLUE then			surface.SetTexture(clip1 == 3 and discmat_blue2 or discmat_blue)		else 			surface.SetTexture(clip1 == 3 and discmat_red2 or discmat_red)		end		for i=1, clip1 do			surface.DrawTexturedRect((w / 2) - 28 - diskdist + (diskdist * (i - 1)) + wdist, h - hdist, 56, 32)		end		surface.SetDrawColor(COLOR_WHITE)		if status == POWERUP_FREEZE then			surface.SetTexture(discmat_frozen)			surface.DrawTexturedRect(w - 96, h - 96, 48, 48)		end	endendfunction GM:HUDShouldDraw(hud)	if table.HasValue(DisableHUD, hud) then		return false	end	return trueend